mapper.update:

update RP_LotteryWaySaleReport set
date=#{date},lottery10023=#{lottery10023},lottery10024=#{lottery10024},
lottery100234=#{lottery100234},lottery10026=#{lottery10026},lottery10030=#{lottery10030},
lottery10039=#{lottery10039}
,lottery10040=#{lottery10040},lottery10041=#{lottery10041},lottery10042=#{lottery10042},lottery10058=#{lottery10058},lottery10059=#{lottery10059},lottery10066=#{lottery10066},lotteryAll=#{lotteryAll},
WHERE id=#{id}

mapper.delete:
delete from RP_LotteryWaySaleReport
WHERE id  in

	#{item.id}

delete from RP_OrderTicketReport
WHERE id  in

	#{item.id}

delete from RP_OrderTicketDiffReport
WHERE id  in

	#{item.id}

mapper.select:
SELECT t.* FROM RP_LotteryWaySaleReport t

	t.date = #{0}

ORDER BY t.date desc

SELECT t.* FROM RP_OrderTicketReport t

	t.date = #{0}

ORDER BY t.date desc

SELECT t.* FROM RP_OrderTicketDiffReport t

	t.date = #{0}

ORDER BY t.date desc

SELECT *, (lottery100234 + lottery10026 + lottery10030 + lottery10039 + lottery10040+ lottery10041+ lottery10042 + lottery10058 + lottery10059 + lottery10066) as lotteryAll FROM( select
date_format(a.ticketTime,'%Y-%m-%d') as date,
date_format(a.sycDate,'%Y-%m-%d') as date,
sum(case when a.lotteryId in ('10023','10024') then a.money else 0 end ) as lottery100234, sum(case when a.lotteryId ='10026' then a.money else 0 end ) as lottery10026, sum(case when a.lotteryId ='10030' then a.money else 0 end ) as lottery10030, sum(case when a.lotteryId ='10039' then a.money else 0 end ) as lottery10039, sum(case when a.lotteryId ='10040' then a.money else 0 end ) as lottery10040, sum(case when a.lotteryId ='10058' then a.money else 0 end ) as lottery10058, sum(case when a.lotteryId ='10059' then a.money else 0 end ) as lottery10059, sum(case when a.lotteryId ='10041' then a.money else 0 end ) as lottery10041, sum(case when a.lotteryId ='10042' then a.money else 0 end ) as lottery10042, sum(case when a.lotteryId ='10066' then a.money else 0 end ) as lottery10066 from
DC_CP_OrderInfo a
DC_CP_TraceOrderInfo a
where 1 = 1 and isValid=1
AND orderType = 1 AND status =3 -- and a.ticketTime like '%${date}%' and a.sycDate=#{date}
and dtype=2 and a.sycDate =#{date}
) as cp

SELECT * FROM RP_LotteryWaySaleReport WHERE 1=1
and date BETWEEN #{param.beginTime} and #{param.endTime}
ORDER BY date desc

SELECT * FROM RP_OrderTicketReport WHERE 1=1
and date BETWEEN #{param.beginTime} and #{param.endTime}
ORDER BY date desc

SELECT * FROM RP_OrderTicketDiffReport WHERE 1=1
and date BETWEEN #{param.beginTime} and #{param.endTime}
ORDER BY date desc
